sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
		// initialize right now
		this.rightnow = new GlideDateTime();
    },

	isDatePast: function(strDate){
			// Create GlideDateTime for the passed in date
			var testdate = new GlideDateTime(strDate);

			// If the testdate is before rightnow, return true, else return false
			if (testdate.before(this.rightnow)) {
				return true;
			}
			else {
				return false;
			}
		},

	isDateToday: function(strDate) {
		// check if the date passed in is today..
    	var testdate = new GlideDateTime(strDate);

		var d1 = this.rightnow.getLocalDate();
		var d2 = testdate.getLocalDate();

	    return (d1.compareTo(d2) == 0);				
	},

    type: 'NeedItUtils'
};
